ang=&quot;ko&quot;&gt; &lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt;사용했다&lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt;. &lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt;하지만&lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt; &lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt;페스트리가&lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt; &lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt;최초로&lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt; &lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt;만들어진&lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt; &lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt;곳은&lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt; &lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt;이집트가&lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt; &lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt;아니라&lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt; &lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt;고대&lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt; &lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt;그리스였다&lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt;. &lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt;고대&lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt; &lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt;그리스를&lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt; &lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt;정복했던&lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt; &lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt;로마인들은&lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt; &lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt;페스트리&lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt; &lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt;속에&lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt; &lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt;많은&lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt; &lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt;재료들을&lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt; &lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt;내용물로&lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt; &lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt;집어넣기&lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt; &lt;/span&gt;&lt;span lang=&quot;ko&quot;&gt;위해서&lt;/span&gt;&lt;span lang=&quot;ko&q